import styled from 'styled-components';

export const BodyStyle = styled.div`
    background: white; 
    font-family: 'Roboto', sans-serif;;
`;


export const CabeceraStyle = styled.div`
    display: flex;
    flex-wrap: wrap;
    flex-direction: column;
    background-color: #ddd;
    border: 1px solid #e5e5e5;
    padding: 1rem;
    margin-bottom: 1rem;

        @media (max-width: 842px) {
            text-align: center;
        }

    span {
        background-color: black;
        color: white;
        padding: 3px 5px;
        border-radius: 5px;
    }
`;

export const ListadoStyle = styled.div`
    display: flex;
    flex-wrap: wrap;
    flex-direction: row;
    justify-content: space-between;
    padding: 1rem;
    @media (max-width: 1900px) {
        flex-wrap: wrap;
    }
`;

export const ItemStyle = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    background-color: #ddd;
    border: 1px solid #e5e5e5;
    border-radius: 5px;
    padding: 1rem;
    margin: 1rem;
    width: 30%;
    @media (max-width: 1900px) {
        width: 40%;
    }
    @media (max-width: 842px) {
        width: 90%;
    }
    h3 {
        text-align: center;
        font-size: 1.2rem;
    }
    p {
        font-size: 1rem;
    }
    h5 {
        font-size: 1.2rem;
        text-align: center;
        margin : 7px;
    }
    h5 {
        font-size: 1rem;
        font-weight: bold;
        text-align: center;
        margin: 7px;
        span {
            background-color:#28FC8E;
            padding: 5px;
            font-weight: lighter;
            color: #7D0B4E ;
            border-radius: 6px;
        }
    }
`;

export const ButtonStyle = styled.button`
       display: inline-block;
                    outline: none;
                    cursor: pointer;
                    font-weight: 600;
                    border-radius: 3px;
                    padding: 12px 24px;
                    border: 0;
                    color: #fff;
                    background: lighterblue;
                    line-height: 1.15;
                    font-size: 16px;
                    :hover {
                        transition: all .1s ease;
                        box-shadow: 0 0 0 0 #fff, 0 0 0 3px #1de9b6;
                    }

`;
